//FUNÇÕES PARA MANTER O SELECT SELECIONADO
function selected( $value, $selected ){
	return $value == $_GET['nome_empresa'] ? ' selected="selected"' : '';
}
function selected_acao( $value, $selected ){
	return $value == $_GET['acao'] ? ' selected="selected"' : '';
}
