function redefineSenha($email) {
    
$mail = new PHPMailer(true);                              // Passing `true` enables exceptions
try {
//    //Server settings
//    $mail->SMTPDebug = 2;                                 // Enable verbose debug output
//    $mail->isSMTP();                                      // Set mailer to use SMTP
//    $mail->Host = 'smtp1.example.com;smtp2.example.com';  // Specify main and backup SMTP servers
//    $mail->SMTPAuth = true;                               // Enable SMTP authentication
//    $mail->Username = 'user@example.com';                 // SMTP username
//    $mail->Password = 'secret';                           // SMTP password
//    $mail->SMTPSecure = 'tls';                            // Enable TLS encryption, `ssl` also accepted
//    $mail->Port = 587;                                    // TCP port to connect to

    $mail->SMTPDebug = false;
    $mail->isSMTP();
    $mail->Host = 'smtp.gmail.com';
    $mail->SMTPAuth = true;
    $mail->SMTPSecure = 'ssl';
    $mail->Username = 'exemple@gmail.com';
    $mail->Password = 'password';
    $mail->Port = 465;
    
    //Recipients
//    $mail->setFrom('from@example.com', 'Mailer');
//    $mail->addAddress('joe@example.net', 'Joe User');     // Add a recipient
//    $mail->addAddress('ellen@example.com');               // Name is optional
//    $mail->addReplyTo('info@example.com', 'Information');
//    $mail->addCC('cc@example.com');
//    $mail->addBCC('bcc@example.com');
    
    $mail->setFrom($mail->Username, 'Suporte da SealSearch');
    $mail->addAddress($email);

//    //Attachments
//    $mail->addAttachment('/var/tmp/file.tar.gz');         // Add attachments
//    $mail->addAttachment('/tmp/image.jpg', 'new.jpg');    // Optional name

    //Content
//    $mail->isHTML(true);                                  // Set email format to HTML
//    $mail->Subject = 'Here is the subject';
//    $mail->Body    = 'This is the HTML message body <b>in bold!</b>';
//    $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';
    
    // define a a lingua e o charset
    $mail->setLanguage('pt');
    $mail->CharSet = "UTF-8";
    
    
    $mail->isHTML(true);
    $mail->Subject = "Pedido de redefinição de senha";
    $mail->Body = "Caro usuário, caso não tenha solicitado redefinição de senha, por favor ignore essa mensagem.<br>";
    $mail->Body .= "Caso tenha solicitado, acesse o link para redefinir sua senha:<br>".''."<br>";
    $mail->Body .= "Esta é uma mensagem automática, por favor não responda a mesma";
    
    $mail->AltBody = "Caro usuário, caso não tenha solicitado redefinição de senha, por favor, ignore essa mensagem.\n";
    $mail->AltBody .= "Caso tenha solicitado, acesse o link para redefinir sua senha:\n".''."\n";
    $mail->AltBody .= "Esta é uma mensagem automática, por favor não responda a mesma";

    $mail->send();
    
    $_SESSION['msgDeVerificacao'] = "Email enviado";
    return true;
    
} catch (Exception $e) {
    
    $_SESSION['msgDeVerificacao'] = 'Não foi possível enviar o email';
    echo 'Messagem não pode ser enviada. Mailer Error: ', $mail->ErrorInfo;
    return false;
}

}

